import React, { useState, useEffect } from 'react';
import ChatInterface from './components/ChatInterface';
import MermaidDiagram from './components/MermaidDiagram';
import { careerAPI } from './services/api';
import { AlertCircle, CheckCircle, Wifi, WifiOff } from 'lucide-react';

function App() {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentDiagram, setCurrentDiagram] = useState('');
  const [backendStatus, setBackendStatus] = useState('unknown');
  const [error, setError] = useState(null);

  // Check backend connection on component mount
  useEffect(() => {
    checkBackendHealth();
  }, []);

  // Debug: Monitor diagram state changes
  useEffect(() => {
    console.log('üìä FRONTEND: currentDiagram state changed to:', currentDiagram ? `"${currentDiagram.substring(0, 50)}..."` : 'empty');
  }, [currentDiagram]);

  const checkBackendHealth = async () => {
    try {
      await careerAPI.testConnection();
      setBackendStatus('connected');
    } catch (error) {
      setBackendStatus('disconnected');
      console.error('Backend connection failed:', error);
    }
  };

  const handleSendMessage = async (messageContent) => {
    if (isLoading) return;

    const userMessage = { role: 'user', content: messageContent };
    const newMessages = [...messages, userMessage];
    setMessages(newMessages);

    setIsLoading(true);
    setError(null);

    try {
      const response = await careerAPI.chat(newMessages);

      const aiMessage = { 
        role: 'ai',
        content: response.response || 'I received your message and am processing it.',
      };
      setMessages(prev => [...prev, aiMessage]);

      if (response.mermaid_chart) {
        setCurrentDiagram(response.mermaid_chart);
      }

      setBackendStatus('connected');

    } catch (error) {
      console.error('Error sending message:', error);

      // Smart error message handling
      let errorMessageText = 'An unknown error occurred. Please try again.';
      if (error.response && error.response.data && error.response.data.detail) {
        // Use the detailed error message from FastAPI
        errorMessageText = error.response.data.detail;
      } else if (error.message) {
        errorMessageText = error.message;
      }

      setError(errorMessageText); // For the toast notification

      const errorMessage = { 
        role: 'ai', 
        content: `Sorry, I encountered an error: ${errorMessageText}`,
      };
      setMessages(prev => [...prev, errorMessage]);

      setBackendStatus('disconnected');
    } finally {
      setIsLoading(false);
    }
  };

  const renderStatusBar = () => {
    const statusConfig = {
      connected: {
        icon: <Wifi className="w-4 h-4" />,
        text: 'Connected',
        bgColor: 'bg-green-100',
        textColor: 'text-green-800',
        borderColor: 'border-green-200'
      },
      disconnected: {
        icon: <WifiOff className="w-4 h-4" />,
        text: 'Backend Disconnected',
        bgColor: 'bg-red-100',
        textColor: 'text-red-800',
        borderColor: 'border-red-200'
      },
      unknown: {
        icon: <AlertCircle className="w-4 h-4" />,
        text: 'Checking Connection...',
        bgColor: 'bg-yellow-100',
        textColor: 'text-yellow-800',
        borderColor: 'border-yellow-200'
      }
    };

    const config = statusConfig[backendStatus];

    return (
      <div className={`flex items-center justify-between px-4 py-2 border-b ${config.borderColor} ${config.bgColor}`}>
        <div className={`flex items-center space-x-2 ${config.textColor}`}>
          {config.icon}
          <span className="text-sm font-medium">{config.text}</span>
        </div>

        {backendStatus === 'disconnected' && (
          <button
            onClick={checkBackendHealth}
            className="text-sm px-3 py-1 bg-red-200 hover:bg-red-300 rounded-md transition-colors"
          >
            Retry
          </button>
        )}

        {backendStatus === 'connected' && (
          <div className="flex items-center space-x-1 text-green-700">
            <CheckCircle className="w-4 h-4" />
            <span className="text-sm">Multi-Agent System Ready</span>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="h-screen bg-gray-50 flex flex-col">
      {/* Status Bar */}
      {renderStatusBar()}

      {/* Main Content */}
      <div className="flex-1 flex overflow-hidden">
        {/* Chat Interface */}
        <div className="w-1/2 border-r border-gray-300 flex flex-col">
          <ChatInterface
            messages={messages}
            isLoading={isLoading}
            onSendMessage={handleSendMessage}
          />
        </div>

        {/* Visualization Panel */}
        <div className="w-1/2 flex flex-col bg-gray-50">
          {/* Header */}
          <div className="flex-shrink-0 bg-white border-b border-gray-200 p-4">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-lg font-semibold text-gray-800">Career Roadmap</h2>
                <p className="text-sm text-gray-600">Visual representation of your career path</p>
              </div>
              <div className="text-2xl">üó∫Ô∏è</div>
            </div>
          </div>

          {/* Diagram Area */}
          <div className="flex-1 p-4 overflow-auto">
            <MermaidDiagram 
              chart={currentDiagram} 
              className="w-full h-full rounded-lg border border-gray-200"
            />
          </div>

          {/* Footer Info */}
          <div className="flex-shrink-0 bg-white border-t border-gray-200 p-3">
            <div className="text-xs text-gray-500 text-center">
              {currentDiagram ? (
                <span className="flex items-center justify-center space-x-1">
                  <CheckCircle className="w-3 h-3 text-green-500" />
                  <span>Roadmap generated by AI agents</span>
                </span>
              ) : (
                'Start a conversation to generate your personalized career roadmap'
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Error Toast */}
      {error && (
        <div className="fixed bottom-4 right-4 bg-red-500 text-white px-6 py-3 rounded-lg shadow-lg">
          <div className="flex items-center space-x-2">
            <AlertCircle className="w-5 h-5" />
            <span>{error}</span>
            <button 
              onClick={() => setError(null)}
              className="ml-2 text-red-200 hover:text-white"
            >
              √ó
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
