import React, { useState, useEffect } from 'react';
import ChatInterface from './components/ChatInterface';
import CareerRoadmapFlow from './components/CareerRoadmapFlow';
import { careerAPI } from './services/api';
import { Wifi, WifiOff } from 'lucide-react';

function App() {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentLearningPath, setCurrentLearningPath] = useState(null);
  const [backendStatus, setBackendStatus] = useState('unknown');

  useEffect(() => {
    checkBackendHealth();
  }, []);

  const checkBackendHealth = async () => {
    try {
      await careerAPI.testConnection();
      setBackendStatus('connected');
    } catch (error) {
      setBackendStatus('disconnected');
    }
  };

  const handleSendMessage = async (message) => {
    if (isLoading) return;
    const userMessage = { type: 'user', content: message };
    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);
    try {
      const context = {
        conversation_history: messages.slice(-6).map(m => ({ role: m.type, content: m.content })),
        current_learning_path: currentLearningPath,
        is_follow_up: messages.length > 0,
      };
      const response = await careerAPI.chat(message, context);
      const aiMessage = { type: 'ai', content: response.response };
      setMessages(prev => [...prev, aiMessage]);
      if (response.data?.learning_path) {
        setCurrentLearningPath(response.data.learning_path);
      }
      setBackendStatus('connected');
    } catch (error) {
      const errorMessage = { type: 'ai', content: `Sorry, an error occurred: ${error.message}` };
      setMessages(prev => [...prev, errorMessage]);
      setBackendStatus('disconnected');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="h-screen w-screen bg-[#0A0A0A] text-gray-100 flex flex-col font-sans">
      {/* Header */}
      <header className="flex-shrink-0 border-b border-gray-800 p-4 flex justify-between items-center">
        <div className="font-semibold text-lg tracking-wider">CareerPath.AI</div>
        <div className={`flex items-center space-x-2 text-sm ${backendStatus === 'connected' ? 'text-green-400' : 'text-red-400'}`}>
          {backendStatus === 'connected' ? <Wifi size={16} /> : <WifiOff size={16} />}
          <span>{backendStatus === 'connected' ? 'Online' : 'Offline'}</span>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-grow flex flex-row overflow-hidden">
        {/* Left Panel - Chat */}
        <div className="w-1/2 flex flex-col border-r border-gray-800">
          <ChatInterface onSendMessage={handleSendMessage} isLoading={isLoading} messages={messages} />
        </div>

        {/* Right Panel - Visualization */}
        <div className="w-1/2 h-full flex flex-col">
          <div className="p-4 border-b border-gray-800">
            <h2 className="text-lg font-semibold">Your Career Roadmap</h2>
            <p className="text-sm text-gray-400">An interactive visualization of your path</p>
          </div>
          <div className="flex-grow p-4 bg-dots">
            <CareerRoadmapFlow learningPath={currentLearningPath} />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
