import React, { useEffect, useRef, useState } from 'react';
import mermaid from 'mermaid';

const MermaidDiagram = ({ chart, className = '' }) => {
  const elementRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!chart) {
      setIsLoading(false);
      return;
    }

    const renderDiagram = async () => {
      try {
        setIsLoading(true);
        setError(null);

        // Initialize mermaid with configuration
        mermaid.initialize({
          startOnLoad: false,
          theme: 'default',
          themeVariables: {
            primaryColor: '#3b82f6',
            primaryTextColor: '#1f2937',
            primaryBorderColor: '#2563eb',
            lineColor: '#6b7280',
            secondaryColor: '#f3f4f6',
            tertiaryColor: '#f9fafb',
            background: '#ffffff',
            mainBkg: '#ffffff',
            secondBkg: '#f8fafc',
            tertiaryBkg: '#f1f5f9',
          },
          flowchart: {
            useMaxWidth: true,
            htmlLabels: true,
            curve: 'basis',
            padding: 20,
            nodeSpacing: 50,
            rankSpacing: 50,
          },
          securityLevel: 'loose',
        });

        if (elementRef.current) {
          // Clear previous content
          elementRef.current.innerHTML = '';

          // Generate unique ID for this diagram
          const id = `mermaid-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

          // Render the diagram
          const { svg } = await mermaid.render(id, chart);
          elementRef.current.innerHTML = svg;
        }
      } catch (err) {
        console.error('Mermaid rendering error:', err);
        setError('Failed to render diagram');
      } finally {
        setIsLoading(false);
      }
    };

    renderDiagram();
  }, [chart]);

  if (!chart) {
    return (
      <div className={`flex items-center justify-center p-8 text-gray-500 ${className}`}>
        <div className="text-center">
          <div className="text-4xl mb-2">📊</div>
          <p>No roadmap available yet</p>
          <p className="text-sm">Start a conversation to generate your career path!</p>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className={`flex items-center justify-center p-8 ${className}`}>
        <div className="text-center">
          <div className="loading-dots mb-4">
            <div style={{'--i': 0}}></div>
            <div style={{'--i': 1}}></div>
            <div style={{'--i': 2}}></div>
          </div>
          <p className="text-gray-600">Generating your career roadmap...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`flex items-center justify-center p-8 text-red-500 ${className}`}>
        <div className="text-center">
          <div className="text-4xl mb-2">⚠️</div>
          <p>{error}</p>
          <p className="text-sm text-gray-500 mt-2">Please try again</p>
        </div>
      </div>
    );
  }

  return (
    <div className={`mermaid-container ${className}`}>
      <div 
        ref={elementRef} 
        className="w-full h-full min-h-[300px] flex items-center justify-center"
      />
    </div>
  );
};

export default MermaidDiagram;
