import React, { useState, useRef, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { Send, Bot } from 'lucide-react';

const ChatInterface = ({ onSendMessage, isLoading, messages = [] }) => {
  const [inputMessage, setInputMessage] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || isLoading) return;
    onSendMessage(inputMessage.trim());
    setInputMessage('');
  };

  const renderMessage = (message, index) => {
    const isUser = message.type === 'user';
    return (
      <div key={index} className={`flex items-start gap-3 my-4 ${isUser ? 'justify-end' : ''}`}>
        {!isUser && (
          <div className="w-8 h-8 rounded-full bg-gray-700 flex items-center justify-center flex-shrink-0">
            <Bot size={20} />
          </div>
        )}
        <div className={`max-w-[75%] rounded-lg px-4 py-2 ${isUser ? 'bg-blue-600 text-white' : 'bg-gray-800'}`}>
          <ReactMarkdown remarkPlugins={[remarkGfm]} className="prose prose-sm prose-invert max-w-none">
            {message.content}
          </ReactMarkdown>
        </div>
      </div>
    );
  };

  const renderTypingIndicator = () => (
    <div className="flex items-start gap-3 my-4">
      <div className="w-8 h-8 rounded-full bg-gray-700 flex items-center justify-center flex-shrink-0">
        <Bot size={20} />
      </div>
      <div className="bg-gray-800 rounded-lg px-4 py-2">
        <div className="loading-dots">
          <div style={{'--i': 0}}></div>
          <div style={{'--i': 1}}></div>
          <div style={{'--i': 2}}></div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="flex flex-col h-full bg-transparent">
      <div className="flex-1 overflow-y-auto p-4 custom-scrollbar">
        {messages.length === 0 && !isLoading && (
          <div className="text-center text-gray-400 mt-8">
            <Bot className="mx-auto h-12 w-12" />
            <h3 className="mt-4 text-lg font-medium">Welcome to CareerPath.AI</h3>
            <p className="mt-2 text-sm">
              Tell me your career goals to get started.
            </p>
          </div>
        )}
        {messages.map(renderMessage)}
        {isLoading && renderTypingIndicator()}
        <div ref={messagesEndRef} />
      </div>
      <div className="border-t border-gray-800 p-4">
        <form onSubmit={handleSubmit} className="flex items-center gap-4">
          <textarea
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSubmit(e);
              }
            }}
            placeholder="Describe your career goals..."
            className="w-full bg-gray-800 text-gray-100 placeholder-gray-400 rounded-md p-3 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="1"
            disabled={isLoading}
          />
          <button type="submit" disabled={!inputMessage.trim() || isLoading} className="bg-blue-600 hover:bg-blue-700 text-white rounded-md p-3 disabled:opacity-50">
            <Send className="h-5 w-5" />
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatInterface;
