import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 30000, // 30 seconds timeout for LLM calls
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    console.log(`Making ${config.method?.toUpperCase()} request to ${config.url}`);
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    console.log(`Response received:`, response.status);
    return response;
  },
  (error) => {
    console.error('API Error:', error.response?.data || error.message);
    return Promise.reject(error);
  }
);

// API functions
export const careerAPI = {
  // Health check
  async healthCheck() {
    try {
      const response = await api.get('/api/health');
      return response.data;
    } catch (error) {
      throw new Error('Backend is not available');
    }
  },

  // Full career planning with history
  async chat(messages) {
    try {
      const response = await api.post('/api/chat', { messages });
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.detail || 'Failed to get chat response');
    }
  },

  // Full career planning
  async createCareerPlan(query) {
    try {
      const response = await api.post('/api/career-plan', query);
      return response.data;
    } catch (error) {
      throw new Error(error.response?.data?.detail || 'Failed to create career plan');
    }
  },

  // Test backend connection
  async testConnection() {
    try {
      const response = await api.get('/');
      return response.data;
    } catch (error) {
      throw new Error('Cannot connect to backend');
    }
  }
};

export default api;
