import React, { useCallback, useState, useEffect } from 'react';
import {
  ReactFlow,
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  Handle,
  Position,
  MarkerType,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "./ui/card";
import { Badge } from "./ui/badge";
import { Sheet, SheetContent, SheetDescription, SheetHeader, SheetTitle } from "./ui/sheet";

// Custom node using shadcn Card component
const PhaseNode = ({ data, selected }) => {
  const isStartNode = data.type === 'start';
  const isEndNode = data.type === 'end';

  return (
    <>
      {!isStartNode && <Handle type="target" position={Position.Top} className="!w-2 !h-2 !bg-gray-400" />}

      <div className={`w-64 bg-gray-900 border-2 rounded-lg p-4 ${selected ? 'border-blue-500 shadow-lg' : 'border-gray-700'} ${isStartNode ? '!border-green-500' : ''} ${isEndNode ? '!border-blue-500' : ''}`}>
        <div className="flex items-center gap-3 mb-2">
          <span className="text-xl">{data.emoji}</span>
          <span className="font-semibold">{data.label}</span>
        </div>
        {data.duration && <div className="text-xs text-gray-400 mb-2">{data.duration}</div>}
        {data.description && (
          <p className="text-sm text-gray-300 border-t border-gray-700 pt-2 mt-2">{data.description}</p>
        )}
        {data.skills && data.skills.length > 0 && (
          <div className="flex flex-wrap gap-2 pt-2 mt-2 border-t border-gray-700">
            {data.skills.map((skill, idx) => (
              <span key={idx} className="text-xs bg-gray-700 px-2 py-1 rounded-full">{skill}</span>
            ))}
          </div>
        )}
      </div>

      {!isEndNode && <Handle type="source" position={Position.Bottom} className="!w-2 !h-2 !bg-gray-400" />}
    </>
  );
};

// Custom edge with progress indicator
const nodeTypes = {
  phaseNode: PhaseNode,
};

const CareerRoadmapFlow = ({ learningPath, className = '' }) => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNode, setSelectedNode] = useState(null);

  const defaultEdgeOptions = {
    animated: true,
    style: {
      strokeWidth: 2,
    },
    markerEnd: {
      type: MarkerType.ArrowClosed,
      width: 20,
      height: 20,
    },
  };

  // Increased node spacing
  const nodeSpacing = 200;

  // Convert learning path data to React Flow nodes and edges
  useEffect(() => {
    if (!learningPath || !learningPath.learning_phases) {
      // Default roadmap when no data
      const defaultNodes = [
        {
          id: 'start',
          type: 'phaseNode',
          position: { x: 50, y: 50 },
          data: { 
            label: 'Start Your Journey', 
            emoji: 'üöÄ',
            type: 'start'
          },
        },
        {
          id: 'end',
          type: 'phaseNode', 
          position: { x: 50, y: 200 },
          data: { 
            label: 'Achieve Your Goal', 
            emoji: 'üéØ',
            type: 'end'
          },
        },
      ];

      const defaultEdges = [
        {
          id: 'start-end',
          source: 'start',
          target: 'end',
          animated: true,
          style: { stroke: '#3b82f6', strokeWidth: 2 },
          markerEnd: {
            type: MarkerType.ArrowClosed,
            width: 25,
            height: 25,
            color: '#3b82f6',
          },
        },
      ];

      setNodes(defaultNodes);
      setEdges(defaultEdges);
      return;
    }

    // Generate nodes from learning phases
    const phaseNodes = [];
    const phaseEdges = [];
    const phases = learningPath.learning_phases;

    console.log('üé® REACT FLOW: Generating nodes from learning path:', {
      phases_count: phases.length,
      timeline: learningPath.timeline,
      has_changes: !!learningPath.changes_made
    });

    // Start node
    phaseNodes.push({
      id: 'start',
      type: 'phaseNode',
      position: { x: 50, y: 50 },
      data: { 
        label: 'Current Role', 
        emoji: 'üë®‚Äçüíª',
        type: 'start'
      },
    });

    // Phase nodes - with improved positioning
    phases.forEach((phase, index) => {
      phaseNodes.push({
        id: `phase-${index}`,
        type: 'phaseNode',
        position: { x: 50, y: 150 + (index * nodeSpacing) },
        data: {
          label: phase.phase || `Phase ${index + 1}`,
          duration: phase.duration,
          skills: phase.skills || [],
          description: phase.description,
          emoji: ['üìö', 'üéì', 'üèÜ', '‚≠ê', 'üöÄ', 'üí°'][index % 6],
        },
      });

      // Edge from previous node - Blue arrows show learning progression
      const sourceId = index === 0 ? 'start' : `phase-${index - 1}`;
      phaseEdges.push({
        id: `edge-${index}`,
        source: sourceId,
        target: `phase-${index}`,
        style: { stroke: '#3b82f6' },
        markerEnd: { color: '#3b82f6' },
      });
    });

    // End node
    phaseNodes.push({
      id: 'end',
      type: 'phaseNode',
      position: { x: 50, y: 150 + (phases.length * nodeSpacing) },
      data: { 
        label: 'Target Role', 
        emoji: 'üéØ',
        type: 'end'
      },
    });

    // Final edge - Green arrow shows goal achievement
    phaseEdges.push({
      id: `edge-final`,
      source: phases.length > 0 ? `phase-${phases.length - 1}` : 'start',
      target: 'end',
      style: { stroke: '#10b981', strokeWidth: 3 },
      markerEnd: {
        width: 25,
        height: 25,
        color: '#10b981',
      },
    });

    // Update with smooth transitions
    setNodes((prevNodes) => {
      // If this is an update and we have previous nodes, try to preserve positions
      if (prevNodes.length > 2) { // More than just start/end
        const nodeMap = new Map(prevNodes.map(node => [node.id, node]));
        return phaseNodes.map(newNode => {
          const existingNode = nodeMap.get(newNode.id);
          if (existingNode) {
            // Preserve position and any user interactions
            return {
              ...newNode,
              position: existingNode.position,
            };
          }
          return newNode;
        });
      }
      return phaseNodes;
    });

    setEdges(phaseEdges);

    // Log the update for debugging
    console.log('üé® REACT FLOW: Updated with', phaseNodes.length, 'nodes and', phaseEdges.length, 'edges');

  }, [learningPath]);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  const onNodeClick = useCallback((event, node) => {
    setSelectedNode(node);
  }, []);

  if (!learningPath) {
    return (
      <div className={`flex items-center justify-center p-8 text-gray-500 ${className}`}>
        <div className="text-center">
          <div className="text-4xl mb-2">üó∫Ô∏è</div>
          <p className="text-lg font-medium">Interactive Career Roadmap</p>
          <p className="text-sm">Start a conversation to generate your personalized path!</p>
        </div>
      </div>
    );
  }

  return (
    <div className={`w-full h-full ${className}`}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={onNodeClick}
        nodeTypes={nodeTypes}
        defaultEdgeOptions={defaultEdgeOptions}
        fitView
        fitViewOptions={{ padding: 0.2 }}
        attributionPosition="bottom-left"
      >
        <Controls />
        <MiniMap 
          nodeStrokeColor={(n) => n.type === 'input' ? '#10b981' : '#3b82f6'}
          nodeColor={(n) => n.data.type === 'start' ? '#dcfce7' : 
                           n.data.type === 'end' ? '#dbeafe' : '#f9fafb'}
          nodeBorderRadius={8}
          className="!bg-gray-50"
        />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      <Sheet open={!!selectedNode} onOpenChange={() => setSelectedNode(null)}>
        <SheetContent>
          <SheetHeader>
            <SheetTitle className="text-2xl flex items-center gap-3">
              {selectedNode?.data.emoji} {selectedNode?.data.label}
            </SheetTitle>
            <SheetDescription>
              {selectedNode?.data.duration}
            </SheetDescription>
          </SheetHeader>
          <div className="py-4 space-y-4">
            <div>
              <h4 className="font-semibold mb-2">Description</h4>
              <p className="text-sm text-muted-foreground">
                {selectedNode?.data.description || "No description provided."}
              </p>
            </div>
            <div>
              <h4 className="font-semibold mb-2">Skills to Develop</h4>
              <div className="flex flex-wrap gap-2">
                {selectedNode?.data.skills?.map((skill, idx) => (
                  <Badge key={idx} variant="outline">{skill}</Badge>
                ))}
              </div>
            </div>
          </div>
        </SheetContent>
      </Sheet>
    </div>
  );
};

export default CareerRoadmapFlow;
